import { exec } from '@qq/utils/basic.js'

export const git_commit = async(files, message) => {

	// FIXME: let's just use typescript for typechecking, m'kay!?
	files = typeof files === 'string' ? [files] : files
	files = files.join(' ')

	await exec(`git add ${files}`)
	await exec(`git commit ${files} -m "ðŸ’  ${message}"`)
}
